var theTime={clockTick:function(e,n){now=new Date,d=now.getSeconds()%2?":":"&nbsp;",document.getElementById(e).innerHTML="<"+n+">"+timer.pad(now.getHours(),2)+"<span>"+d+"</span>"+timer.pad(now.getMinutes(),2)+"</"+n+">"},setupClock:function(e,n){timer.cronAdd(1e3,function(){theTime.clockTick(e,n)},!0)}};